<?php$route = '/simple-api/3-factor-x/';$app->get($route, function ()  use ($app){	if(isset($_REQUEST['query'])){ $query = $_REQUEST['query']; } else { $query = '';}	$ObjectText = file_get_contents('https://raw.github.com/simple-api/api-3-factor-x/gh-pages/3-factor-x.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 1;		$id = $Object['id'];		$x = $Object['x'];		$xfactor_1 = $Object['xfactor_1'];		$xfactor_2 = $Object['xfactor_2'];		$xfactor_3 = $Object['xfactor_3'];		if($query!=''){		$IncludeRecord = 0;			if(strpos(strtolower($x),strtolower($query)) === 0 || strpos(strtolower($xfactor_1),strtolower($query)) === 0 || strpos(strtolower($xfactor_2),strtolower($query)) === 0 || strpos(strtolower($xfactor_3),strtolower($query)) === 0)				{				$IncludeRecord = 1;				}			}		if($IncludeRecord==1)			{			$F = array();			$F['id'] = $id;			$F['x'] = $x;			$F['xfactor_1'] = $xfactor_1;			$F['xfactor_2'] = $xfactor_2;			$F['xfactor_3'] = $xfactor_3;			array_push($ReturnObject, $F);			}		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});$route = '/simple-api/3-factor-x/:id';$app->get($route, function ($IncomingID)  use ($app){	$ObjectText = file_get_contents('https://raw.github.com/simple-api/api-3-factor-x/gh-pages/3-factor-x.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 0;		$id = $Object['id'];		$x = $Object['x'];		$xfactor_1 = $Object['xfactor_1'];		$xfactor_2 = $Object['xfactor_2'];		$xfactor_3 = $Object['xfactor_3'];		if($IncomingID==$id)			{			$IncludeRecord=1;			}		if($IncludeRecord==1)			{			$F = array();			$F['id'] = $id;			$F['x'] = $x;			$F['xfactor_1'] = $xfactor_1;			$F['xfactor_2'] = $xfactor_2;			$F['xfactor_3'] = $xfactor_3;			array_push($ReturnObject, $F);			}		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});