<?php$route = '/simple-api/people/';$app->get($route, function ()  use ($app){	if(isset($_REQUEST['query'])){ $query = $_REQUEST['query']; } else { $query = '';}	$ObjectText = file_get_contents('https://raw.github.com/simple-api/api-people/gh-pages/people.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 1;		$id = $Object['id'];		$first_name = $Object['first_name'];		$last_name = $Object['last_name'];		$title = $Object['title'];		$phone = $Object['phone'];		$email = $Object['email'];		if($query!=''){		$IncludeRecord = 0;			if(strpos(strtolower($first_name),strtolower($query)) === 0 || strpos(strtolower($last_name),strtolower($query)) === 0 || strpos(strtolower($title),strtolower($query)) === 0)				{				$IncludeRecord = 1;				}			}		if($IncludeRecord==1)			{			$F = array();			$F['id'] = $id;			$F['first_name'] = $first_name;			$F['last_name'] = $last_name;			$F['title'] = $title;			$F['phone'] = $phone;			$F['email'] = $email;			array_push($ReturnObject, $F);			}		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});$route = '/simple-api/people/:id';$app->get($route, function ($IncomingID)  use ($app){	$ObjectText = file_get_contents('https://raw.github.com/simple-api/api-people/gh-pages/people.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 0;		$id = $Object['id'];		$first_name = $Object['first_name'];		$last_name = $Object['last_name'];		$title = $Object['title'];		$phone = $Object['phone'];		$email = $Object['email'];		if($IncomingID==$id)			{			$IncludeRecord=1;			}		if($IncludeRecord==1)			{			$F = array();			$F['id'] = $id;			$F['first_name'] = $first_name;			$F['last_name'] = $last_name;			$F['title'] = $title;			$F['phone'] = $phone;			$F['email'] = $email;			array_push($ReturnObject, $F);			}		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});