<?php$route = '/simple-api/press/';$app->get($route, function ()  use ($app){	if(isset($_REQUEST['query'])){ $query = $_REQUEST['query']; } else { $query = '';}	$ObjectText = file_get_contents('https://raw.github.com/simple-api/api-press/gh-pages/press.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 1;				$id = $Object['id'];		$title = $Object['title'];		$date = $Object['date'];		$tags = $Object['tags'];		$url = $Object['url'];		$body = $Object['body'];		if($query!=''){		$IncludeRecord = 0;			if(strpos(strtolower($title),strtolower($query)) === 0 || strpos(strtolower($tags),strtolower($query)) === 0 || strpos(strtolower($body),strtolower($query)) === 0)				{				$IncludeRecord = 1;				}			}		if($IncludeRecord==1)			{			$F = array();			$F['id'] = $id;			$F['title'] = $title;			$F['date'] = $date;			$F['tags'] = $tags;			$F['url'] = $url;			$F['body'] = $body;			array_push($ReturnObject, $F);			}		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});$route = '/simple-api/press/:id';$app->get($route, function ($IncomingID)  use ($app){	$ObjectText = file_get_contents('https://raw.github.com/simple-api/api-press/gh-pages/press.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 0;				$id = $Object['id'];		$title = $Object['title'];		$date = $Object['date'];		$tags = $Object['tags'];		$url = $Object['url'];		$body = $Object['body'];		if($IncomingID==$id)			{			$IncludeRecord=1;			}		if($IncludeRecord==1)			{			$F = array();			$F['id'] = $id;			$F['title'] = $title;			$F['date'] = $date;			$F['tags'] = $tags;			$F['url'] = $url;			$F['body'] = $body;			array_push($ReturnObject, $F);			}		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});